function base64Encode(str) {
  const base64EncodeChars =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";
  let out = "",
    i = 0,
    len = str.length;
  let c1, c2, c3;

  while (i < len) {
    c1 = str.charCodeAt(i++) & 0xff;
    if (i == len) {
      out += base64EncodeChars.charAt(c1 >> 2);
      out += base64EncodeChars.charAt((c1 & 0x3) << 4);
      out += "==";
      break;
    }

    c2 = str.charCodeAt(i++);
    if (i == len) {
      out += base64EncodeChars.charAt(c1 >> 2);
      out += base64EncodeChars.charAt(((c1 & 0x3) << 4) | ((c2 & 0xf0) >> 4));
      out += base64EncodeChars.charAt((c2 & 0xf) << 2);
      out += "=";
      break;
    }

    c3 = str.charCodeAt(i++);
    out += base64EncodeChars.charAt(c1 >> 2);
    out += base64EncodeChars.charAt(((c1 & 0x3) << 4) | ((c2 & 0xf0) >> 4));
    out += base64EncodeChars.charAt(((c2 & 0xf) << 2) | ((c3 & 0xc0) >> 6));
    out += base64EncodeChars.charAt(c3 & 0x3f);
  }

  return out;
}

function encodeBase64() {
  const input = components.input1.value;
  const encodedValue = base64Encode(input);
  return encodedValue;
}

return { encodedValue: encodeBase64() };

function base64Decode(str) {
  const base64DecodeChars =
    "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789+/";
  let c1, c2, c3, c4;
  let i = 0,
    len = str.length,
    out = "";

  while (i < len) {
    do {
      c1 = base64DecodeChars.indexOf(str.charAt(i++));
    } while (i < len && c1 == -1);
    if (c1 == -1) break;

    do {
      c2 = base64DecodeChars.indexOf(str.charAt(i++));
    } while (i < len && c2 == -1);
    if (c2 == -1) break;

    out += String.fromCharCode((c1 << 2) | ((c2 & 0x30) >> 4));

    do {
      c3 = str.charAt(i++);
      if (c3 == "=") return out;
      c3 = base64DecodeChars.indexOf(c3);
    } while (i < len && c3 == -1);
    if (c3 == -1) break;

    out += String.fromCharCode(((c2 & 0xf) << 4) | ((c3 & 0x3c) >> 2));

    do {
      c4 = str.charAt(i++);
      if (c4 == "=") return out;
      c4 = base64DecodeChars.indexOf(c4);
    } while (i < len && c4 == -1);
    if (c4 == -1) break;

    out += String.fromCharCode(((c3 & 0x03) << 6) | c4);
  }

  return out;
}

function decodeBase64() {
  const input = components.input1.value;
  const decodedValue = base64Decode(input);
  return decodedValue;
}
return { decodedValue: decodeBase64() };
